        <ArticleItem
          :deleteArticle="deleteArticle"
          :article="article"
          :index="index"
        />
      </div>
    </div>
  </div>
</template>

<script>
import ArticleItem from "./ArticleItem.vue";
import { mapState } from "vuex";

